svg.script:
// alert('Загрузка');

let схема = document.getElementById('svg8');
let четверти = схема.getElementsByClassName('четверть');
let столб = схема.getElementById('столб');
let горизонт = схема.getElementById('горизонт');


столб.addEventListener('click', выбор);
горизонт.addEventListener('click', выбор);

for (let i = 0; i < четверти.length; i++) {
	четверти[i].addEventListener('click', выбор);
}

let направление = document.getElementById('направление');
направление.addEventListener('click', function (e) {
	if ('asc' === схема.dataset.direction) {
		схема.dataset.direction = 'desc';
	} else {
		схема.dataset.direction = 'asc';
	}
});


function выбор(e) {
	let выбраннаяОсь = e.target.parentNode;

	столб.classList.remove('выбранная');
	горизонт.classList.remove('выбранная');

	for (let i = 0; i < четверти.length; i++) {
		четверти[i].classList.remove('выбранная');
	}

	выбраннаяОсь.classList.add('выбранная');
}

